<?phpinclude_once(__DIR__.'/Classes/PHPExcel.php');$objPHPExcel = new PHPExcel();$objPHPExcel->setActiveSheetIndex(0)			->setCellValue('A1', '年/月')			->setCellValue('B1', '期別')			->setCellValue('C1', '縣市')			->setCellValue('D1', '地區')			->setCellValue('E1', '部落')			->setCellValue('F1', '總使用人次')			->setCellValue('G1', '總使用人數')			->setCellValue('H1', '總使用時間(分)')			->setCellValue('I1', '總流量(MB)')			->setCellValue('J1', '總上行流量(MB)')			->setCellValue('K1', '總下行流量(MB)');$objWriter = new PHPExcel_Writer_Excel2007($objPHPExcel);$objWriter->save('1234.xlsx');?>